import React, { useEffect, useState } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { 
  Layout, 
  Menu, 
  Button, 
  Input, 
  Avatar, 
  Dropdown, 
  Space, 
  Badge,
  Divider
} from 'antd';
import type { MenuProps } from 'antd';
import { 
  HomeOutlined, 
  CompassOutlined, 
  BellOutlined, 
  UserOutlined, 
  EditOutlined,
  LogoutOutlined,
  HeartOutlined,
  StarOutlined,
  SettingOutlined,
  SearchOutlined
} from '@ant-design/icons';
import useUserStore from '../../stores/userStore';
import './style.scss';

const { Header } = Layout;
const { Search } = Input;

// 头部导航组件
const AppHeader: React.FC = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { userInfo, isLoggedIn, logout } = useUserStore();
  const [current, setCurrent] = useState(location.pathname);

  // 监听路由变化，更新当前选中菜单
  useEffect(() => {
    setCurrent(location.pathname);
  }, [location.pathname]);

  // 处理菜单点击
  const handleMenuClick = (e: { key: string }) => {
    setCurrent(e.key);
    navigate(e.key);
  };

  // 处理搜索
  const handleSearch = (value: string) => {
    if (value.trim()) {
      navigate(`/search?keyword=${encodeURIComponent(value.trim())}`);
    }
  };

  // 处理登出
  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  // 用户菜单项
  const userMenuItems: MenuProps['items'] = [
    {
      key: '1',
      label: <Link to={`/user/${userInfo?.id}`}>个人主页</Link>,
      icon: <UserOutlined />
    },
    {
      key: '2',
      label: <Link to="/user/likes">我的点赞</Link>,
      icon: <HeartOutlined />
    },
    {
      key: '3',
      label: <Link to="/user/collections">我的收藏</Link>,
      icon: <StarOutlined />
    },
    {
      key: 'divider',
      type: 'divider'
    },
    {
      key: '4',
      label: <Link to="/settings">设置</Link>,
      icon: <SettingOutlined />
    },
    {
      key: '5',
      label: '退出登录',
      icon: <LogoutOutlined />,
      onClick: handleLogout
    },
  ];

  return (
    <Header className="app-header">
      <div className="header-wrapper">
        {/* Logo */}
        <div className="logo">
          <Link to="/">
            <img src="data:image/png;base64," alt="小红书" />
            <span>小红书</span>
          </Link>
        </div>

        {/* 主导航
        <Menu
          mode="horizontal"
          selectedKeys={[current]}
          onClick={handleMenuClick}
          className="main-menu"
          items={[
            {
              key: '/',
              icon: <HomeOutlined />,
              label: '首页'
            },
            {
              key: '/explore',
              icon: <CompassOutlined />,
              label: '发现'
            }
          ]}
        /> */}

        {/* 搜索框 */}
        <div className="search-box">
          <Search
            placeholder="搜索内容、用户、标签"
            allowClear
            onSearch={handleSearch}
            className="search-input"
            prefix={<SearchOutlined />}
          />
        </div>

        {/* 右侧功能区 */}
        <div className="header-right">
          {isLoggedIn ? (
            <>
              {/* 发布按钮 */}
              <Button 
                type="primary" 
                shape="round" 
                icon={<EditOutlined />}
                onClick={() => navigate('/publish')}
                className="publish-btn"
              >
                发布
              </Button>

              {/* 消息通知 */}
              <Badge count={5} size="small">
                <Button 
                  type="text" 
                  icon={<BellOutlined />} 
                  onClick={() => navigate('/notifications')}
                  className="notification-btn"
                />
              </Badge>

              {/* 用户头像和下拉菜单 */}
              <Dropdown menu={{ items: userMenuItems }} placement="bottomRight">
                <div className="user-avatar">
                  <Avatar src={userInfo?.avatar} icon={<UserOutlined />} />
                </div>
              </Dropdown>
            </>
          ) : (
            <Space>
              <Button onClick={() => navigate('/login')}>登录</Button>
              <Button type="primary" onClick={() => navigate('/register')}>注册</Button>
            </Space>
          )}
        </div>
      </div>
    </Header>
  );
};

export default AppHeader; 